import React, { useEffect, useRef } from 'react';
import { View, Text, StyleSheet, Animated, Easing, Platform, Image } from 'react-native';

// Import your custom icons
const stockIcons = {
  AAPL: require('./assets/apple-icon.png'),
  GOOGL: require('./assets/google-icon.png'),
  MSFT: require('./assets/microsoft-icon.png'),
  AMZN: require('./assets/amazon-icon.png'),
  FB: require('./assets/facebook-icon.png'),
};

const stockData = [
  { symbol: 'AAPL', price: '150.25', change: '+1.2%' },
  { symbol: 'GOOGL', price: '2750.80', change: '-0.5%' },
  { symbol: 'MSFT', price: '305.10', change: '+0.8%' },
  { symbol: 'AMZN', price: '3300.45', change: '+0.3%' },
  { symbol: 'FB', price: '335.20', change: '-0.7%' },
];

const StockTicker = () => {
  const scrollX = useRef(new Animated.Value(0)).current;
  const containerWidth = useRef(0);

  const startAnimation = () => {
    const tickerWidth = stockData.length * 1500; // Adjust based on your item width
    const duration = stockData.length * 5000; // Adjust speed as needed

    scrollX.setValue(0); // Reset to start position

    Animated.timing(scrollX, {
      toValue: -tickerWidth,
      duration: duration,
      easing: Easing.linear,
      useNativeDriver: Platform.OS !== 'web',
    }).start(({ finished }) => {
      if (finished) {
        startAnimation(); // Restart the animation when it finishes
      }
    });
  };

  useEffect(() => {
    startAnimation();
    return () => scrollX.stopAnimation(); // Clean up on unmount
  }, []);

  return (
    <View 
      style={styles.container}
      onLayout={(event) => {
        containerWidth.current = event.nativeEvent.layout.width;
      }}
    >
      <Animated.View
        style={[
          styles.tickerContainer,
          {
            transform: [
              {
                translateX: scrollX.interpolate({
                  inputRange: [-containerWidth.current, 0],
                  outputRange: [0, containerWidth.current],
                  extrapolate: 'clamp',
                }),
              },
            ],
          },
        ]}
      >
        {[...stockData, ...stockData].map((stock, index) => (
          <View key={index} style={styles.stockItem}>
            <Image 
              source={stockIcons[stock.symbol]} 
              style={styles.icon} 
              resizeMode="contain"
            />
            <Text style={styles.price}>{stock.price}</Text>
            <Text
              style={[
                styles.change,
                { color: stock.change.startsWith('+') ? 'green' : 'red' },
              ]}
            >
              {stock.change}
            </Text>
          </View>
        ))}
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    height: 50,
    backgroundColor: '#000',
    overflow: 'hidden',
    width: '100%',
  },
  tickerContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  stockItem: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingHorizontal: 10,
    width: 150,
  },
  icon: {
    width: 24,
    height: 24,
    marginRight: 10,
  },
  price: {
    color: '#fff',
    marginRight: 5,
  },
  change: {
    fontWeight: 'bold',
  },
});

export default StockTicker;
