import React, { useEffect, useRef } from 'react';
import { View, Text, StyleSheet, Animated, Easing, Platform, Image } from 'react-native';

// Import your custom icons
const stockIcons = {
    AAPL: require('./assets/apple-icon.png'),
    GOOGL: require('./assets/google-icon.png'),
    MSFT: require('./assets/microsoft-icon.png'),
    AMZN: require('./assets/amazon-icon.png'),
    FB: require('./assets/facebook-icon.png'),
  };

const stockData = [
  { symbol: 'AAPL', price: '150.25', change: '+1.2%' },
  { symbol: 'GOOGL', price: '2750.80', change: '-0.5%' },
  { symbol: 'MSFT', price: '305.10', change: '+0.8%' },
  { symbol: 'AMZN', price: '3300.45', change: '+0.3%' },
  { symbol: 'FB', price: '335.20', change: '-0.7%' },
];

const StockTicker = () => {
  const scrollX = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    const tickerWidth = stockData.length * 150;
    const duration = stockData.length * 5000;

    Animated.loop(
      Animated.timing(scrollX, {
        toValue: -tickerWidth,
        duration: duration,
        easing: Easing.linear,
        useNativeDriver: true,
      })
    ).start();
  }, []);

  return (
    <View style={styles.container}>
      <Animated.View
        style={[
          styles.tickerContainer,
          {
            transform: [{ translateX: scrollX }],
          },
        ]}
      >
        {stockData.map((stock, index) => (
          <View key={index} style={styles.stockItem}>
            <Text style={styles.symbol}>{stock.symbol}</Text>
            <Text style={styles.price}>{stock.price}</Text>
            <Text
              style={[
                styles.change,
                { color: stock.change.startsWith('+') ? 'green' : 'red' },
              ]}
            >
              {stock.change}
            </Text>
          </View>
        ))}
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    height: 50,
    backgroundColor: '#000',
    overflow: 'hidden',
  },
  tickerContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  stockItem: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingHorizontal: 10,
    width: 150,
  },
  symbol: {
    color: '#fff',
    fontWeight: 'bold',
    marginRight: 5,
  },
  price: {
    color: '#fff',
    marginRight: 5,
  },
  change: {
    fontWeight: 'bold',
  },
});

export default StockTicker;
