import React, { useEffect, useRef, useState } from 'react';
import { View, Text, StyleSheet, Animated, Easing, Platform, Image, Dimensions } from 'react-native';

// Import your custom icons
const stockIcons = {
  AAPL: require('./assets/apple-icon.png'),
  GOOGL: require('./assets/google-icon.png'),
  MSFT: require('./assets/microsoft-icon.png'),
  AMZN: require('./assets/amazon-icon.png'),
  FB: require('./assets/facebook-icon.png'),
};

const stockData = [
  { symbol: 'AAPL', price: '150.25', change: '+1.2%' },
  { symbol: 'GOOGL', price: '2750.80', change: '-0.5%' },
  { symbol: 'MSFT', price: '305.10', change: '+0.8%' },
  { symbol: 'AMZN', price: '3300.45', change: '+0.3%' },
  { symbol: 'FB', price: '335.20', change: '-0.7%' },
];

const ITEM_WIDTH = 150; // Width of each stock item
const SCREEN_WIDTH = Dimensions.get('window').width;

const StockTicker = () => {
  const [contentWidth, setContentWidth] = useState(0);
  const scrollX = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    const totalWidth = stockData.length * ITEM_WIDTH;
    setContentWidth(totalWidth);

    const animation = Animated.loop(
      Animated.timing(scrollX, {
        toValue: -totalWidth,
        duration: totalWidth * 30, // Adjust speed here
        easing: Easing.linear,
        useNativeDriver: true,
      })
    );

    animation.start();

    return () => animation.stop();
  }, []);

  const tickerContent = [...stockData, ...stockData]; // Duplicate content for seamless loop

  return (
    <View style={styles.container}>
      <Animated.View
        style={[
          styles.tickerContainer,
          {
            transform: [
              {
                translateX: scrollX.interpolate({
                  inputRange: [-contentWidth, 0],
                  outputRange: [0, contentWidth],
                  extrapolate: 'clamp',
                }),
              },
            ],
            width: contentWidth * 2, // Double width for duplicated content
          },
        ]}
      >
        {tickerContent.map((stock, index) => (
          <View key={index} style={styles.stockItem}>
            <Image 
              source={stockIcons[stock.symbol]} 
              style={styles.icon} 
              resizeMode="contain"
            />
            <Text style={styles.price}>{stock.price}</Text>
            <Text
              style={[
                styles.change,
                { color: stock.change.startsWith('+') ? 'green' : 'red' },
              ]}
            >
              {stock.change}
            </Text>
          </View>
        ))}
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    height: 50,
    backgroundColor: '#000',
    overflow: 'hidden',
    width: SCREEN_WIDTH,
  },
  tickerContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  stockItem: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingHorizontal: 10,
    width: ITEM_WIDTH,
  },
  icon: {
    width: 24,
    height: 24,
    marginRight: 10,
  },
  price: {
    color: '#fff',
    marginRight: 5,
  },
  change: {
    fontWeight: 'bold',
  },
});

export default StockTicker;
