import React, { useState, useCallback } from 'react';
import { View, Text, StyleSheet, TextInput, Button } from 'react-native';

const StockInputPanel = ({ onAddStock }) => {
  const [symbol, setSymbol] = useState('');
  const [price, setPrice] = useState('');
  const [change, setChange] = useState('');
  const [dragActive, setDragActive] = useState(false);

  const handleDrag = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
  }, []);

  const handleDragIn = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(true);
  }, []);

  const handleDragOut = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
  }, []);

  const handleDrop = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      const file = e.dataTransfer.files[0];
      const reader = new FileReader();
      reader.onload = (event) => {
        try {
          const jsonData = JSON.parse(event.target.result);
          setSymbol(jsonData.symbol || '');
          setPrice(jsonData.price || '');
          setChange(jsonData.change || '');
        } catch (error) {
          console.error('Error parsing JSON:', error);
        }
      };
      reader.readAsText(file);
    }
  }, []);

  const handleAddStock = () => {
    if (symbol && price && change) {
      onAddStock({ symbol, price, change });
      setSymbol('');
      setPrice('');
      setChange('');
    }
  };

  return (
    <View 
      style={[styles.panel, dragActive && styles.dragActive]}
      onDragEnter={handleDragIn}
      onDragLeave={handleDragOut}
      onDragOver={handleDrag}
      onDrop={handleDrop}
    >
      <Text style={styles.dragText}>Drag and drop stock file here</Text>
      <TextInput
        style={styles.input}
        placeholder="Stock Symbol"
        value={symbol}
        onChangeText={setSymbol}
      />
      <TextInput
        style={styles.input}
        placeholder="Price"
        value={price}
        onChangeText={setPrice}
        keyboardType="numeric"
      />
      <TextInput
        style={styles.input}
        placeholder="Change (e.g. +1.2%)"
        value={change}
        onChangeText={setChange}
      />
      <Button title="Add Stock" onPress={handleAddStock} />
    </View>
  );
};

const styles = StyleSheet.create({
  panel: {
    width: 250,
    padding: 20,
    backgroundColor: '#f0f0f0',
    borderWidth: 2,
    borderColor: '#ccc',
    borderStyle: 'dashed',
  },
  dragActive: {
    borderColor: '#007bff',
  },
  dragText: {
    textAlign: 'center',
    marginBottom: 10,
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
});

export default StockInputPanel;
