import React, { useEffect, useRef, useState } from 'react';
import { View, Text, StyleSheet, Animated, Easing, Image, Dimensions } from 'react-native';

const stockIcons = {
  AAPL: require('./assets/apple-icon.png'),
  GOOGL: require('./assets/google-icon.png'),
  MSFT: require('./assets/microsoft-icon.png'),
  AMZN: require('./assets/amazon-icon.png'),
  FB: require('./assets/facebook-icon.png'),
};

const initialStockData = [
  { symbol: 'AAPL', price: '150.25', change: '+1.2%' },
  { symbol: 'GOOGL', price: '2750.80', change: '-0.5%' },
  { symbol: 'MSFT', price: '305.10', change: '+0.8%' },
  { symbol: 'AMZN', price: '3300.45', change: '+0.3%' },
  { symbol: 'FB', price: '335.20', change: '-0.7%' },
];

const ITEM_WIDTH = 150;
const SCREEN_WIDTH = Dimensions.get('window').width;
const BUFFER_SIZE = Math.ceil(SCREEN_WIDTH / ITEM_WIDTH) + 1;

const StockTicker = () => {
  const [stockData, setStockData] = useState([]);
  const scrollX = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    // Initialize the circular buffer
    setStockData(Array(BUFFER_SIZE).fill(null).map((_, index) => ({
      ...initialStockData[index % initialStockData.length],
      key: index,
    })));

    const animate = () => {
      Animated.timing(scrollX, {
        toValue: -ITEM_WIDTH,
        duration: 3000, // Adjust for speed
        easing: Easing.linear,
        useNativeDriver: true,
      }).start(({ finished }) => {
        if (finished) {
          scrollX.setValue(0);
          setStockData(prevData => {
            const newData = [...prevData];
            const firstItem = newData.shift();
            newData.push({
              ...initialStockData[firstItem.key % initialStockData.length],
              key: firstItem.key + BUFFER_SIZE,
            });
            return newData;
          });
          animate();
        }
      });
    };

    animate();

    return () => scrollX.stopAnimation();
  }, []);

  return (
    <View style={styles.container}>
      <Animated.View
        style={[
          styles.tickerContainer,
          {
            transform: [{ translateX: scrollX }],
          },
        ]}
      >
        {stockData.map((stock) => (
          <View key={stock.key} style={styles.stockItem}>
            <Image 
              source={stockIcons[stock.symbol]} 
              style={styles.icon} 
              resizeMode="contain"
            />
            <Text style={styles.price}>{stock.price}</Text>
            <Text
              style={[
                styles.change,
                { color: stock.change.startsWith('+') ? 'green' : 'red' },
              ]}
            >
              {stock.change}
            </Text>
          </View>
        ))}
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    height: 500,
    backgroundColor: '#000',
    overflow: 'hidden',
    width: SCREEN_WIDTH,
  },
  tickerContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  stockItem: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingHorizontal: 10,
    width: ITEM_WIDTH,
  },
  icon: {
    width: 24,
    height: 24,
    marginRight: 10,
  },
  price: {
    color: '#fff',
    marginRight: 5,
  },
  change: {
    fontWeight: 'bold',
  },
});

export default StockTicker;
