import React, { useState, useEffect, useRef } from 'react';
import { View, Text, StyleSheet, Animated, Easing, Image, TextInput, Button, ScrollView, Dimensions } from 'react-native';

const stockIcons = {
  AAPL: require('./assets/apple-icon.png'),
  GOOGL: require('./assets/google-icon.png'),
  MSFT: require('./assets/microsoft-icon.png'),
  AMZN: require('./assets/amazon-icon.png'),
  FB: require('./assets/facebook-icon.png'),
};

const initialStockData = [
  { symbol: 'AAPL', price: '150.25', change: '+1.2%' },
  { symbol: 'GOOGL', price: '2750.80', change: '-0.5%' },
  { symbol: 'MSFT', price: '305.10', change: '+0.8%' },
  { symbol: 'AMZN', price: '3300.45', change: '+0.3%' },
  { symbol: 'FB', price: '335.20', change: '-0.7%' },
];

const ITEM_WIDTH = 180;
const SCREEN_WIDTH = Dimensions.get('window').width;
const BUFFER_SIZE = Math.ceil(SCREEN_WIDTH / ITEM_WIDTH) + 1;

const StockInputPanel = ({ onAddStock }) => {
  const [symbol, setSymbol] = useState('');
  const [price, setPrice] = useState('');
  const [change, setChange] = useState('');

  const handleAddStock = () => {
    if (symbol && price && change) {
      onAddStock({
        symbol,
        price,
        change,
      });
      setSymbol('');
      setPrice('');
      setChange('');
    }
  };

  return (
    <View style={styles.panel}>
      <TextInput
        style={styles.input}
        placeholder="Stock Symbol"
        value={symbol}
        onChangeText={setSymbol}
      />
      <TextInput
        style={styles.input}
        placeholder="Price"
        value={price}
        onChangeText={setPrice}
        keyboardType="numeric"
      />
      <TextInput
        style={styles.input}
        placeholder="Change (e.g. +1.2%)"
        value={change}
        onChangeText={setChange}
      />
      <Button title="Add Stock" onPress={handleAddStock} />
    </View>
  );
};

const StockTicker = ({ stocks }) => {
  const [stockData, setStockData] = useState([]);
  const scrollX = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    const initializeStockData = () => {
      setStockData(Array(BUFFER_SIZE).fill(null).map((_, index) => ({
        ...stocks[index % stocks.length],
        key: index,
      })));
    };

    initializeStockData();

    const animate = () => {
      Animated.timing(scrollX, {
        toValue: -ITEM_WIDTH,
        duration: 3000,
        easing: Easing.linear,
        useNativeDriver: true,
      }).start(({ finished }) => {
        if (finished) {
          scrollX.setValue(0);
          setStockData(prevData => {
            const newData = [...prevData];
            const firstItem = newData.shift();
            newData.push({
              ...stocks[firstItem.key % stocks.length],
              key: firstItem.key + BUFFER_SIZE,
            });
            return newData;
          });
          animate();
        }
      });
    };

    animate();

    return () => scrollX.stopAnimation();
  }, [stocks]);

  return (
    <View style={styles.tickerContainer}>
      <Animated.View
        style={[
          styles.tickerContent,
          {
            transform: [{ translateX: scrollX }],
          },
        ]}
      >
        {stockData.map((stock) => (
          <View key={stock.key} style={styles.stockItem}>
            <Image 
              source={stockIcons[stock.symbol] || require('./assets/favicon.png')}
              style={styles.icon} 
              resizeMode="contain"
            />
            <View style={styles.textContainer}>
              <Text style={styles.symbol}>{stock.symbol}</Text>
              <Text style={styles.price}>{stock.price}</Text>
              <Text
                style={[
                  styles.change,
                  { color: stock.change.startsWith('+') ? 'green' : 'red' },
                ]}
              >
                {stock.change}
              </Text>
            </View>
          </View>
        ))}
      </Animated.View>
    </View>
  );
};

const StockTickerApp = () => {
  const [stocks, setStocks] = useState(initialStockData);

  const handleAddStock = (newStock) => {
    setStocks(prevStocks => [...prevStocks, newStock]);
  };

  return (
    <View style={styles.container}>
      <StockInputPanel onAddStock={handleAddStock} />
      <StockTicker stocks={stocks} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
  },
  panel: {
    width: 250,
    padding: 20,
    backgroundColor: '#f0f0f0',
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
  tickerContainer: {
    flex: 1,
    height: 70,
    backgroundColor: '#000',
    overflow: 'hidden',
  },
  tickerContent: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  stockItem: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingHorizontal: 10,
    width: ITEM_WIDTH,
  },
  icon: {
    width: 40,
    height: 40,
    marginRight: 10,
  },
  textContainer: {
    flexDirection: 'column',
  },
  symbol: {
    color: '#fff',
    fontWeight: 'bold',
    fontSize: 16,
  },
  price: {
    color: '#fff',
    fontSize: 14,
  },
  change: {
    fontWeight: 'bold',
    fontSize: 12,
  },
});

export default StockTickerApp;
